import React from 'react';


const ICONS = {
    'visibility': 'data:image/svg+xml;base64,',
    'visibility off': 'data:image/svg+xml;base64,'
};


const Icon = (props) => {
    const { size, icon, onClick } = props;
    return (
        <div className="Icon"
             onClick={onClick}
             style={{
                width: size,
                height: size,
                backgroundImage: `url(${ICONS[icon]})`
        }}/>
    )
};

export default Icon;